import React, { Component } from 'react';
import './App.css';
import ToolBar from './components/ToolBar.js'
import MessageList from './components/MessageList.js'

class App extends Component {
  state = {

  }

  updateMessage = async(data) => {
    await fetch('http://localhost:8082/api/messages',{
      method: 'PATCH',
      body: JSON.stringify(data),
      headers:{
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      }
    })
    let response = await fetch('http://localhost:8082/api/messages')
    const messages = await response.json()
    let newState = {
      messages:[...messages]
    }
    newState.messages = newState.messages.map(message => {
      const m = {...message}; m.selected = false; return m
    })
    this.setState(newState)
  }

  componentDidMount = async() => {
    const response = await fetch('http://localhost:8082/api/messages')
    const messages = await response.json()
    let newState = {
      messages: [...messages]
    }
    newState.messages.forEach(message => message.selected = false)
    this.setState(newState)
  }

  delete = async() => {
    let selected = this.state.messages.filter(message => message.selected)
    let idToDelete = selected.map(message => message.id)
    const response = await fetch('http://localhost:8082/api/messages', {
      method: "PATCH",
      body: JSON.stringify({
        messageIds: [...idToDelete],
        command: 'delete',
      }),
      headers:{
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      }
    })
    const message = await response.json()
    let newState = {
      messages: [...message]
    }
    newState.messages.forEach(message => message['selected'] = false)
    console.log(message)
    this.setState(newState)
  }

  select = (e, id) => {
    let newMessages = [...this.state.messages]
    const idx = newMessages.findIndex(message => message.id === id)
    newMessages[idx]['selected'] = e.target.checked
    this.setState({
      messages: newMessages
    })
  }

  starMessage = async(id) => {
    let newMessages = [...this.state.messages]
    const idx = newMessages.findIndex(message => message.id === id)
    let isStarred = newMessages[`${idx}`]['starred'] || false
    isStarred ? newMessages[idx]['starred'] = false : newMessages[idx]['starred'] = true
    console.log('Test')
    await this.updateMessage({
      messageIds: [id],
      command: 'star',
      star: !isStarred
    })
  }

  render() {
    return (
      <div className="App">
        <h1 className="text-center">Cooper's Bootleg gmail :D</h1>
        <div className="container">
          <ToolBar delete={this.delete}/>
          <MessageList starMessage={this.starMessage} select={this.select} messages={this.state.messages} loadMessages={this.loadMessages}/>
        </div>
      </div>
    );
  }
}

export default App;
