import React, { Component } from 'react';
import './App.css';
import ToolBar from './components/ToolBar.js'
import MessageList from './components/MessageList.js'

class App extends Component {
  state = {}

  componentDidMount = async() => {
    const response = await fetch('http://localhost:8082/api/messages')
    const messages = await response.json()
    let newState = {messages: [...messages]}
    newState.messages.forEach(message => message.selected = false)
    this.setState(newState)
  }

  delete = async() => {
    let selected = this.state.messages.filter(message => message.selected)
    let idToDelete = selected.map(message => message.id)
    const response = await fetch('http://localhost:8082/api/messages', {
      method: "PATCH",
      body: JSON.stringify({
        messageIds: [...idToDelete],
        command: 'delete',
      }),
      headers:{
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      }
    })
    const message = await response.json()
    let newState = {messages: [...message]}
    newState.messages.forEach(message => message['selected'] = false)
    console.log(newState)
    this.setState(newState)
  }

  starMessage = (event) => {
    console.log(this.state)
  }

  render() {
    return (
      <div className="App">
        <h1 className="text-center">Cooper's Bootleg gmail :D</h1>
        <div className="container">
          <ToolBar delete={this.delete}/>
          <MessageList starMessage={this.starMessage} messages={this.state.messages} loadMessages={this.loadMessages}/>
        </div>
      </div>
    );
  }
}

export default App;
